package Test3;

public class Series {
// 10. Write a program to accept a number from user and generate following series till that
	//number. 1, 2 , 1 , 3 , 2 , 5 , 3, 8 , 5, 13 , 8 , 21,

	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=1;
      int num=5;
    for(int i=1;i<num;i++) {
    	int k=a;
    	System.out.print(k+",");
    	int b=a+i;
    	System.out.print(b+",");
    	
    }
      }
	

}
