package AssignmentPatterns;

public class TwentyOne {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
  for(int i=0;i<=4;i++) {
	  for(int j=0;j<=4;j++) {
		  if (i==j)
			  System.out.print(j+" ");
		  else
			  System.out.print("0 ");
	  }
	  System.out.println();
  }
	}

}
