package Demo;

public class Atif {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    for(int i=1;i<=6;i++) {
    	if(i%2==0) {
    		for(char j=1;j<=i;j++) {
    			System.out.print((char)(j+64));
    		}
    		System.out.println();
    	}
    	
    		else {
    			for(int k=1;k<=i;k++) {
    				System.out.print(k);
    			}
    		}
    	System.out.println();
    	}
    
    
	}	
}
